xrx:service.xrx:body:
  {
  let $username := request:get-parameter('username', '')
  let $password := request:get-parameter('password', '')
  let $refer := 
    if(matches(request:get-parameter('refer', ''), 'protected-page')) then concat(conf:param('request-root'), 'my-archive')
    else if(matches(request:get-parameter('refer', ''), 'registration-successful')) then '/'
    else request:get-parameter('refer', conf:param('request-root'))
  let $old-user-exists := 
   collection(conf:param('old-user-db-base-uri'))/*:user[./*:username=$username]
  let $old-firstname := $old-user-exists//*:firstname/text() 
  let $old-lastname := $old-user-exists//*:name/text()
  let $old-username := 
    if($old-user-exists) then $old-user-exists//*:username/text() else()
  let $user-exists :=
   collection(conf:param('xrx-user-db-base-uri'))/xrx:user[./xrx:email=$username]
  let $db-user-exists := system:as-user('admin', conf:param('dba-password'), xmldb:exists-user($username))
  let $login := 
   if($user-exists) then xmldb:login(conf:param('atom-db-base-uri'), $username, $password, true()) else false()
  let $copy-parameters := 
    string-join(
	    for $param in request:get-parameter-names()
	    return
	    if (not(matches($param, '(status|old|username|password|service-name|betterform-flag|content|request-relative-path|atomid)'))) then concat('&', $param, '=', request:get-parameter($param, ''))
	    else ''
	  , ''
	  )
  return
  if($old-user-exists and not($user-exists)) then
    response:redirect-to(xs:anyURI(concat(conf:param('request-root'), 'login2?status=1&old=', $old-username, $copy-parameters)))
  else if($user-exists and not($db-user-exists)) then
    response:redirect-to(xs:anyURI(concat(conf:param('request-root'), 'login2?status=2&old=', $old-username, $copy-parameters)))
  else if(not($user-exists)) then
    response:redirect-to(xs:anyURI(concat(conf:param('request-root'), 'login2?status=3&old=', $old-username, $copy-parameters)))
  else if(not($login)) then
    response:redirect-to(xs:anyURI(concat(conf:param('request-root'), 'login2?status=4&old=', $old-username, $copy-parameters)))
  else if($login) then
    response:redirect-to(xs:anyURI($refer))
  else
    response:redirect-to(xs:anyURI(concat(conf:param('request-root'), 'login2?status=5&old=', $old-username, $copy-parameters)))
  }
