xrx:widget.xrx:view:
  {
  	(: request parameter :)
    let $charter-atomid := request:get-parameter('id', '')
    let $backlink := request:get-parameter('backlink', '')
    let $other-id := request:get-parameter('other-id','') 
    let $rev-id := request:get-parameter('rev', '')

    (: public charter :)
    let $atom-tag-name := conf:param('atom-tag-name')
    let $fond-or-collection := charter:context($charter-atomid, $atom-tag-name)
    let $archive-id := charter:archid($charter-atomid, $atom-tag-name)
    let $fond-id := charter:fondid($charter-atomid, $atom-tag-name)
    let $collection-id := charter:collectionid($charter-atomid, $atom-tag-name)
    let $public-base-collection := 
    	if($fond-or-collection = 'fond') then 
    		metadata:base-collection('charter', ($archive-id, $fond-id), 'public')
    	else 
        metadata:base-collection('charter', $collection-id, 'public')

    (: If Revision-id is submitted: Use Versioning-Object as saved Charter :)
    let $public-entry := $public-base-collection//atom:id[.=$charter-atomid]/parent::atom:entry
    let $public-doc := doc(base-uri($public-entry))

    (: saved charter :)
    let $saved-base-collection := metadata:base-collection('charter', 'saved')
    let $published-base-collection := metadata:base-collection('charter', 'public')   

    let $saved-entry :=
      if($rev-id = "" ) then

        if( $other-id = "" ) then 
          $saved-base-collection//atom:id[.=$charter-atomid]/parent::atom:entry
        else
          $published-base-collection//atom:id[.=$other-id]/parent::atom:entry

      else
        root(v:doc($public-doc, xs:int($rev-id)))//atom:entry



    (: version difference :)
    let $xslt := i18n:translate-xml($xrx:db-base-collection/xsl:stylesheet[@id='cei2versiondiff'])
    let $saved-as-text := transform:transform($saved-entry/atom:content/cei:text, $xslt, ())
    let $public-as-text := transform:transform($public-entry/atom:content/cei:text, $xslt, ())
    let $diff := text:diff($public-as-text, $saved-as-text)

    return






